function draw() {
  // width = width*0.7;
  // height = height*0.7;
  resizeCanvas(canvas*resize, canvas*resize)
  // background(colorGroup.get("background"));

  // radial gradiant  
  // gradient = drawingContext.createRadialGradient(width/2, height*0.6, canvas*0.1, width/2, height*0.5, canvas*0.5);
  // gradient.addColorStop(0.1, colorGroup.get("bgcenter"));
  // gradient.addColorStop(0.6, colorGroup.get("background"));
  // gradient.addColorStop(0.8, colorGroup.get("background"));
  // gradient.addColorStop(1, colorGroup.get("background"));
  // drawingContext.fillStyle = gradient;
  // drawingContext.fillRect(0, 0, width, height);

  // mountain and river
	randomSeed(seed);
	background(colorbgA);
	fill(255);
	grad = drawingContext.createLinearGradient(0, 0, 0, height);
	grad.addColorStop(0, colorbg);
	grad.addColorStop(1, colorbg2);
	drawingContext.fillStyle = grad;
	noStroke();
	rectMode(CORNER);
	rect(0, 0, width, height);


	//mountain and river
	noStroke();
	let mountain_h = height / int(random(10, 30));
	for (let n = 0; n < height; n += mountain_h) {
		push();
		translate(0, height - n);

		if (n > height / 3) {
			// mountain
			grad = drawingContext.createLinearGradient(0, -mountain_h, 0, mountain_h);
			grad.addColorStop(0, random(color1));
			grad.addColorStop(1, random(color2));
			drawingContext.fillStyle = grad;
			beginShape();
			curveVertex(-n, n);
			for (let i = xOff; i < width + 100; i += 50) {
				let p = random(-50, 50);
				curveVertex(i, sin(i) * p);
			}
			curveVertex(width + n, n);
			endShape(CLOSE);
		} else {
			//river
			grad = drawingContext.createLinearGradient(0, -mountain_h, 0, mountain_h);
			grad.addColorStop(0, random(colorsR));
			grad.addColorStop(1, random(colorsR2));
			drawingContext.fillStyle = grad;
			beginShape();
			curveVertex(-n, n);
			for (let i = xOff; i < width - xOff; i += 200) {
				let p = random(-1, 1);
				curveVertex(i, cos(i) * p * random(50));
			}
			curveVertex(width + n, n);
			endShape(CLOSE);
		}
		pop();
	}


	//frame
	noFill();
	let colors_frames = "be132d-980f24-af011c-800000-730d0d".split("-").map((a) => "#" + a);
	stroke(random(colors_frames));
	strokeWeight(margin * 1);
	rectMode(CENTER);
	// rect(width / 2, height / 2, width, height);
	image(overAllTexture, 0, 0);
	noLoop();

  scale(resize);

  // drawCoordinate();
  drawHatBody();
  drawFlag1();
  drawFlag2();
  drawFlag3();
  drawEarOutline();
  drawEarInline();
  drawHatDecBand();
  drawHatBase();
  drawHatDecLine();
  drawHatCover();
  drawHatDecBallLeft();
  drawHatDecBallMiddle();
  drawHatDecBallRight();
  drawShoes();
  drawSkirtHem();
  drawClothe();
  drawCuffs();
  drawSleeves();
  drawCoat();
  drawCloak();
  drawCloakTie2();
  drawCloakTie1();
  drawFace();
  drawEyeLeft();
  drawEyeRight();
  drawEyebrowsLeft1();
  drawEyebrowsLeft2();
  drawEyebrowsLeft3();
  drawEyebrowsRight1();
  drawEyebrowsRight2();
  drawEyebrowsRight3();
  drawEyebrowsCenter();
  drawCheekLeft();
  drawCheekRight();
  drawNose();
  drawMouth();

  noLoop();
  seed = parseInt(random(10000));
  let name = "turye-" + seed + ".png";
  // save(name);
}





